    private void btnGravarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGravarActionPerformed
        //Clique do bot√£o gravar
        p.setNome(txtNome.getText());
        p.setCpf(txtCPF.getText());
        p.setSexo(cbxSexo.getSelectedItem().toString().charAt(0));
        p.setIdade(Integer.parseInt(txtIdade.getText()));
        JOptionPane.showMessageDialog(this, "Gravado com sucesso!");
        txtNome.setText("");
        txtCPF.setText("");
        txtIdade.setText("");
    }//GEN-LAST:event_btnGravarActionPerformed

    private void btnImprimirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnImprimirActionPerformed
        JOptionPane.showMessageDialog(this, p.imprimir());
    }//GEN-LAST:event_btnImprimirActionPerformed

